import{_ as p,c as e,o as a,a as t}from"./app.efa20a37.js";const u='{"title":"\u8131\u53E3\u79C0","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u671F\u672B\u8003\u8BD5\u7684\u9B45\u529B","slug":"\u671F\u672B\u8003\u8BD5\u7684\u9B45\u529B"}],"relativePath":"other/article/2022.1.20-talk-show-wow.md","lastUpdated":1642680591729}',r={},s=t('<h1 id="\u8131\u53E3\u79C0" tabindex="-1">\u8131\u53E3\u79C0 <a class="header-anchor" href="#\u8131\u53E3\u79C0" aria-hidden="true">#</a></h1><h2 id="\u671F\u672B\u8003\u8BD5\u7684\u9B45\u529B" tabindex="-1">\u671F\u672B\u8003\u8BD5\u7684\u9B45\u529B <a class="header-anchor" href="#\u671F\u672B\u8003\u8BD5\u7684\u9B45\u529B" aria-hidden="true">#</a></h2><p>\u540C\u5B66\u4EEC\uFF0C\u65F6\u81F3\u4ECA\u65E5\uFF0C\u6211\uFF0C\u5FEB\u8003\u5B8C\u62C9\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u6211\u7684\u8003\u8BD5\uFF0C\u4E5F\u5FEB\u5B8C\u4E86\u3002</p><p>\u8BF7\u914C\u60C5\u5206\u6790\u5206\u6790\u8FD9\u4E24\u53E5\u8BDD\u7684\u4E0D\u540C\u4E4B\u5904\uFF0Ctherefore\uFF0C\u8FD9\u671F\u89C6\u9891\u7684\u4E00\u751F\u4E4B\u654C\u663E\u800C\u6613\u89C1\u3002</p><p>\u6211\u4EEC\u76F4\u63A5\u5BF9\u671F\u672B\u8003\u8BD5\u8FDB\u884C\u6709\u5931\u504F\u9887\u3002</p><p>\u5982\u679C\u8BF4\uFF0C\u4E66\u7C4D\u662F\u4EBA\u7C7B\u8FDB\u6B65\u7684\u9636\u68AF\u3002\u90A3\u8003\u8BD5\uFF0C\u5219\u662F\u6211\u4EEC\u610F\u8BC6\u5230\u8FD9\u4E2A\u9636\u68AF\uFF08xasfasdfsdf\uFF09\u5C31\u7279\u55B5\u722C\u4E0D\u4E0A\u53BB\u3002</p><p>\u8FDB\u5316\u8BBA\u5199\u5230\u6211\u8FD9\u5C31\u7B97\u662F\u8FDB\u5316\u4E0D\u4E0B\u53BB\u4E86\u5C5E\u4E8E\u662F\u3002</p><p>\u8003\u8BD5\u7684\u9B45\u529B\u554A\uFF0C\u53EA\u6709\u7ECF\u5386\u8FC7\u8003\u8BD5\u5468\u7684\u4EBA\u624D\u61C2\u3002</p><p>\u90A3\u611F\u89C9\u5C31\u50CF\u662F\u524D\u5341\u591A\u5468\u8BA9\u4F60\u5B89\u5FC3\u6CE1\u811A\uFF0C\u563F\u563F\uFF0C\u6700\u540E\u4E24\u5468\u8BA9\u4F60\u628A\u6D17\u811A\u6C34\u7ED9\u559D\u4E0B\u53BB\u3002</p><p>\u4E4B\u524D\u5F85\u6211\u76F8\u656C\u5982\u5BBE\uFF0C\u6700\u540E\u4E24\u5468\u8981\u6211\u72D7\u547D\u5440\u3002</p><p>\u6211\u539F\u6765\u751A\u81F3\u4E00\u5929\u8003\u8FC7\u4E09\u95E8\u79D1\uFF0C\u8003\u5B8C\u540E\u554A\uFF0C\u6211\u5C31\u89C9\u5F97\u6700\u8BE5\u653E\u5728\u8BB2\u53F0\u4E0A\u4E0E\u8003\u8BD5\u65E0\u5173\u7684\u4E1C\u897F\u4E0D\u8BE5\u662F\u4E66\u5305\uFF0C\u8BE5\u662F\u6211\u3002</p><p>\u5728\u8FD9\u77ED\u77ED\u7684\u4E00\u5929\u5185\u554A\uFF0C\u6211\u82E5\u6709\u82E5\u65E0\u7684\u5927\u8111\u89C1\u8BC1\u8FC7\u5386\u53F2\u6545\u4E8B\u7684\u7F16\u5199\uFF0C\u6570\u5B66\u516C\u5F0F\u7684\u6539\u9020\uFF0C\u903B\u8F91\u601D\u7EF4\u7684\u98A0\u8986\uFF0C\u4EBA\u6587\u601D\u60F3\u7684\u521B\u65B0\u3002</p><p>\u522B\u4EBA\u7B54\u9898\u53EA\u7528\u60F3\u8C61\uFF0C\u6211\u7B54\u9898\u5927\u6982\u7279\u55B5\u4E3B\u8981\u7528\u7684\u5C31\u662F\u60F3\u8C61\u3002</p><p>\u4FD7\u8BDD\u8BF4\u7684\u597D\uFF0C\u533A\u533A\u4E00\u4E2A\u671F\u672B\u8003\u8BD5\u4F60\u6015\u5565\u5B50\u5440\u3002\u4ED6\u5C31\u6765\u5565\u5B50\u5440\u3002</p><p>\u5927\u5B66\u671F\u672B\u8003\u8BD5\u6700\u6478\u4E0D\u900F\u7684\u4E24\u4E2A\u4E1C\u897F\uFF0C\u51FA\u5377\u7684\u8303\u56F4\uFF0C\u5F00\u5377\u7684\u96BE\u5EA6\u54CE\u5440\u3002</p><p>\u6709\u4E9B\u79D1\u76EE\u4ED6\u8003\u8BD5\u8303\u56F4\u5E7F\u7684\uFF0C\u4F60\u751A\u81F3\u4E0D\u6562\u53BB\u558A\u8001\u5E08\u7ED9\u4F60\u753B\u8303\u56F4\u3002\u5BB3\u6015\u4F60\u628A\u4E66\u52FE\u51FA\u706B\u82B1\u554A\u3002</p><p>\u7136\u540E\u4F60\u4E00\u95EE\u8001\u5E08\uFF1A\u8001\u5E08\uFF0C\u548B\u4EEC\u8FD9\u4E2A\u8003\u8BD5\u8303\u56F4\u662F\u5565\u5B50\uFF0C\u4ED6\u6765\u53E5\uFF1A\u8FD9\u672C\u4E66\u3002</p><p>\u4E0D\u7136\u561E\uFF0C\u4E0D\u7136\u561E\uFF0C\u4F60\u8FD8\u80FD\u8003\u51FA\u8FD9\u672C\u4E66\u4E0D\u6210\uFF1F\u76F4\u5230\u6211\u9047\u5230\u5F00\u5377\uFF0C\u6709\u4E9B\u79D1\u76EE\u7684\u5F00\u5377\uFF0C\u5377\u662F\u7ED9\u4F60\u5F00\u4E86\uFF0C\u4F46\u751C\u7F8E\u4E0D\u8003\u5377\u4E0A\u7684\u5440\u3002</p><p>\u4F60\u90FD\u7FFB\u51FA\u9759\u7535\u4E86\u4F60\u7FFB\u4E66\u3002\u6307\u7EB9\u90FD\u5FEB\u78E8\u8131\u4E86\uFF0C\u4F60\u6253\u6B7B\u627E\u4E0D\u5230\u8FD9\u4E2A\u77E5\u8BC6\u70B9\u5728\u54EA\u91CC\u3002</p><p>\u501F\u7528\u8FC5\u54E5\u4E8C\u7684\u4E00\u6BB5\u8BDD\u6765\u8BF4\uFF0C\u6211\u7FFB\u5F00\u8BFE\u672C\u4E00\u5F00\uFF0C\u8FD9\u8BFE\u672C\u6CA1\u5F97\u7B54\u6848\uFF0C\u6B6A\u6B6A\u659C\u659C\u7684\u6BCF\u9875\u4E0A\u90FD\u5199\u5230\u201C\u5F00\u5377\u201D\u4E24\u4E2A\u5B57\uFF0C</p><p>\u6211\u6A2A\u7AD6\u5199\u4E0D\u6765\uFF0C\u4ED4\u7EC6\u76EF\u4E86\u4E24\u4E2A\u949F\u5934\uFF0C\u624D\u4ECE\u5B57\u7F1D\u5494\u5494\u91CC\u5934\u76EF\u51FA\u5B57\u6765\uFF0C\u6EE1\u7BC7\u5199\u7684\u90FD\u662F\u4E24\u4E2A\u5B57\uFF0C\u6302\u79D1\u3002</p><p>\u76EE\u524D\u5BF9\u6211\u800C\u8A00\uFF0C\u8003\u8BD5\u5468\u6700\u4E3B\u8981\u7684\u77DB\u76FE\uFF0C\u4F9D\u65E7\u662F\u5148\u8FDB\u7684\u8BFE\u672C\u77E5\u8BC6\u9700\u8981\u540C\u6211\u843D\u540E\u7684\u8111\u58F3\u4E4B\u95F4\u7684\u77DB\u76FE\u3002</p><p>\u5C31\u662F\u6211\u6000\u7591\u6211\u6B65\u5165\u5927\u5B66\u540E\u6211\u8111\u9614\u662F\u4E0D\u662F\u8BEF\u4EE5\u4E3A\u6211\u6B65\u5165\u665A\u5E74\u4E86\u54DF\u3002\u4E00\u5929\u4E0D\u5982\u4E00\u5929\u3002</p><p>\u5C31\u6211\u73B0\u5728\u8111\u9614\u7684\u9000\u5316\u7A0B\u5EA6\u554A\uFF0C\u5DF2\u7ECF\u5FEB\u628A\u6211\u903C\u51FA\u7075\u957F\u7C7B\u52A8\u7269\u7FA4\u4F53\u5566\u3002</p><p>\u5C24\u5176\u662F\u8BA1\u7B97\u80FD\u529B\u65B9\u9762\uFF0C\u54CE\uFF0C\u6211\u6709\u65F6\u5019\u558A\u4ED6\u53E3\u7B97\u4E2A\u51E0\u5341\u4E0A\u767E\u7684\u52A0\u51CF\u6CD5\uFF0C\u4ED6\u90FD\u76F4\u63A5\u53EB\u6211\u5217\u7AD6\u5F0F\u8BA1\u7B97\u3002</p><p>\u66F4\u79BB\u8C31\u7684\u662F\u6211\u4E0A\u4E86\u4E00\u5B66\u671F\u7684\u4E66\uFF0C\u4ED6\u4F9D\u7136\u4E0D\u6653\u5F97\u6211\u5728\u54EA\u4EF6\u6559\u5BA4\u4E0A\u8BFE\u3002\u554A\uFF1F\u4F60\u8111\u9614\u4E5F\u662F\u8FD9\u6837\u554A\u3002\u3002</p><p>\u800C\u6700\u8BA9\u6211\u75DB\u82E6\u7684\u83AB\u8FC7\u4E8E\u8003\u8BD5\u590D\u4E60\uFF0C\u6240\u8C13\u590D\u4E60\uFF0C\u5C31\u662F\u628A\u4F60\u4E0D\u4F1A\u7684\u77E5\u8BC6\u70B9\uFF0C\u7FFB\u5F00\u4E66\u786E\u8BA4\u4E00\u4E0B\uFF0C\u6328\uFF0C\u786E\u5B9E\u4E0D\u4F1A</p><p>\u6240\u4EE5\uFF0C\u4F60\u4EEC\u590D\u4E60\u8981\u9002\u5EA6\u554A\uFF0C\u8981\u4E0D\u7136\u4F1A\u5BF9\u8003\u8BD5\u62B1\u6709\u5E0C\u671B\u3002</p><p>\u6211\u6653\u5F97\u6709\u4E9B\u540C\u5B66\u590D\u4E60\uFF0C\u7FFB\u5F00\u4E66\uFF0C\u56AF\u54DF\uFF0C\u9A6C\u51AC\u6885\u561B\u3002</p><p>\u5173\u4E0A\u4E66\uFF0C\u54CE\uFF0C\u9A6C\u5565\u5B50\u6885\u561E\uFF1F</p><p>\u7FFB\u5F00\u4E66\uFF0C\u54E6\uFF0C\u9A6C\u51AC\u6885\uFF1B</p><p>\u5173\u4E0A\u4E66\uFF0C\u5565\u5B50\u51AC\u6885\u561E\uFF1F</p><p>\u6211\u5C31\u4E0D\u4E00\u6837\u4E86\u554A\uFF0C\u7FFB\u5F00\u4E66\uFF0C\u8FD9\u4E09\u4E2A\u5B57\u548B\u5FF5\u561E\u3002</p><p>\u800C\u4E14\u5404\u4F4D\u6653\u5F97\uFF0C\u4E16\u95F4\u7684\u79CD\u79CD\uFF0C\u5728\u4F60\u5F00\u59CB\u590D\u4E60\u7684\u65F6\u5019\uFF0C\u90FD\u4F1A\u53D8\u5F97\u683C\u5916\u6709\u8DA3\u8D77\u6765\u3002</p><p>\u4F60\u4F1A\u53D1\u73B0\u7A97\u5916\u7684\u666F\u8272\u7ADF\u5982\u6B64\u8FF7\u4EBA\uFF0C\u7A7A\u6C14\u597D\u4E0D\u9999\u751C\uFF0C\u6307\u7532\u4E0D\u77E5\u4F55\u65F6\u957F\u7684\u5DF2\u7ECF\u4E0D\u5F97\u4E0D\u51CF\u4E86\u3002</p><p>\u800C\u624B\u673A\uFF0C\u4E5F\u4F1A\u83AB\u540D\u5176\u5999\u7684\u8DF3\u5230\u624B\u4E0A\u6765\u3002\u8FD9\u4E9B\u554A\uFF0C\u6211\u90FD\u4E0D\u6446\u4E86\u3002</p><p>\u6211\u5C31\u60F3\u95EE\u4E00\u53E5\uFF0C\u4E3A\u5565\u5B50\u4E00\u5230\u8003\u8BD5\u5565\u5B50\u74DC\u90FD\u6765\u4E86\uFF0C\u53C8\u5927\u53C8\u751C\u53C8\u8BF1\u4EBA\u3002</p><p>\u6211\u771F\u7684\u662Fcan\u2019t control myselft\u554A\u3002</p><p>\u8868\u9762\u4E0A\u6211\u662F\u505A\u5230\u81EA\u4E60\u5BA4\u7684\u8003\u8BD5\u72D7\uFF0C\u5B9E\u9645\u4E0A\u6211\u662F\u6708\u4E0B\u74DC\u7530\u7684\u4E00\u6839\u78B4\uFF0C</p><p>\u7EB5\u89C2\u6211\u7684\u5B66\u4E60\u751F\u6DAF\uFF0C\u6211\u53D1\u73B0\uFF0C\u9664\u4E86\u6211\u6B63\u5728\u5E72\u7684\u4E8B\u60C5\uFF0C\u6211\u5E72\u5176\u4ED6\u6742\u4E8B\u90FD\u5F88\u4E13\u5FC3\u3002</p><p>\u95F4\u6B47\u6027\u8E0C\u8E87\u6EE1\u5FD7\uFF0C\u6301\u7EED\u6027\u788C\u788C\u65E0\u4E3A\u3002\u554A\u5565\u5B50\uFF01\u4F60\u4E5F\u662F</p><p>\u76F8\u4FE1\u5404\u4F4D\u521A\u8FDB\u5927\u5B66\u7684\u65F6\u5019\uFF0C\u90FD\u4E00\u5B9A\u7ACB\u8FC7\u7C7B\u4F3C\u7684flag\u5427\uFF0C\u6211\u4E00\u5B9A\u8981\u5B66\u4F1Aps\uFF0Cpr\uFF0C</p><p>\u5230\u4E86\u5927\u56DB\uFF0C\u54CE\u8FD8\u597D\u554A\uFF0C\u5B66\u4E86\u4E00\u529E\u554A\uFF0C\u5B66\u4E86\u4E2Ap\u3002</p><p>\u8001\u8BDD\u8BF4\u7684\u597D\uFF0C\u5B66\u4E2A\u5FC5\u526A\u4E0D\u9999\u5356\u3002</p><p>\u7528\u4F60\u90A3\u4E2A\u95F4\u6B47\u6027\u8E0C\u8E87\u6EE1\u5FD7\u7684\u65F6\u95F4\u5C31\u5B66\u4F1A\u4E86\u3002</p><p>\u770B\u561B\uFF0C\u6B63\u5728\u7528\u5FC5\u526A\u7ED9\u4F60\u4EEC\u526A\u89C6\u9891\uFF0C\u5FC5\u526A\u91CC\u5934\u529F\u80FD\u4E30\u5BCC\u5F97\u5F88\u5450\uFF0C</p><p>\u53EF\u4EE5\u8BED\u97F3\u52A0\u5B57\u5E55\uFF0C\u8FD8\u53EF\u4EE5\u52A0\u4E2A\u4E09\u8FDE\u6697\u793A\u67D0\u4E9B\u767D\u5AD6\u7C89\u4E1D\uFF0C\u6700\u540E\u8FD8\u53EF\u4EE5\u76F4\u63A5\u4E0A\u4F20\u6295\u7A3Fb\u7AD9\uFF0C</p><p>\u4F5C\u4E3Ab\u7AD9\u552F\u4E00\u51FA\u54C1\u7684\u7535\u8111\u7AEF\u526A\u8F91\u8F6F\u4EF6\uFF0C\u5FC5\u526A\u786E\u5B9E\u4E0D\u5931\u504F\u9887\u554A</p>',48),o=[s];function _(c,d,n,i,h,l){return a(),e("div",null,o)}var m=p(r,[["render",_]]);export{u as __pageData,m as default};
